mapper.select:
SELECT  
FROM gg_fans 
WHERE FansId = #{fansId} AND DelFlag = '1' AND UserId IN(
	SELECT UserId FROM gg_jokesg WHERE Id = #{jokesgId}
)

SELECT COUNT(Id) FROM gg_fans WHERE DelFlag = '1'

	AND UserId = #{userId} 


	AND FansId = #{fansId}

mapper.update:
UPDATE gg_fans SET
	UpdateBy = #{updateBy},
	UpdateDate = #{updateDate},
	DelFlag = #{delFlag}
WHERE UserId = #{userId} AND FansId = #{fansId}
